    public static String[] getButtonNames() {
        return buttonNames;
    }

    public static String[] getTabs(int buttonID) {
        return tabNames.get(buttonID).toArray(new String[tabNames.get(buttonID).size()]);
    }

    public static String getTopicName(int menuID) {
        return topics.get(menuID).name;
    }

    public static String getArticleContent(int buttonID, int position) {
        return topics.get(buttonID).articles.get(position).content;
    }

    public static List<Integer> getImageIDsFromGroupID(int imageGroupID) {
        switch (imageGroupID) {
            case HEADER_GROUP_ID:
                return imageResIDs.get(HEADER_GROUP_ID);
            default:
                throw new RuntimeException(TAG + " requested nonexistent image group id");
        }
    }

    public static int getImageResIDFromGroupAndPosition(int imageGroupID, int position) {
        return getImageIDsFromGroupID(imageGroupID).get(position);
    }

    public static String getImageUrl(int position) {
        switch (position) {
            case 0:
                return "http://hradcicva.sk/images/cicvazapadna.jpg";
            case 1:
                return "http://hradcicva.sk/images/10fotka1csr.jpg";
            default:
                return "http://hradcicva.sk/images/cicvaletecky.jpg";
        }
    }
